// 获取到小程序页卡整个内容的正则表达式
const reg1 = /<mp-miniprogram (.*)<\/mp-miniprogram>/gm;

// 获取小程序的appid
const reg2 = /data-miniprogram-appid="(.*?)"/gm;

// 获取小程序链接到的path
const reg3 = /data-miniprogram-path="(.*?)"/gm;

// 获取小程序的昵称
const reg4 = /data-miniprogram-nickname="(.*?)"/gm;

// 获取小程序的头像
const reg5 = /data-miniprogram-avatar="(.*?)"/gm;

// 获取小程序页卡的标题
const reg6 = /data-miniprogram-title="(.*?)"/gm;

// 获取小程序页卡图片地址
const reg7 = /data-miniprogram-imageurl="(.*?)"/gm;

// 获取小程序页卡类型
const reg8 = /data-miniprogram-type="(.*?)"/gm;

// 获取小程序的服务类型
const reg9 = /data-miniprogram-servicetype="(.*?)"/gm;

const newsToHtml = (content = '') => {
  // 匹配到的小程序页卡
  let mp_miniprogram_list = content.match(reg1);
  let result = [];
  if (mp_miniprogram_list) {
    result = mp_miniprogram_list.map((item, index) => {
      // 小程序appid
      let appid = item.match(reg2);
      // 小程序连接到的路径
      let path = item.match(reg3);
      // 小程序的昵称
      let nickname = item.match(reg4);
      // 小程序的头像
      let avatar = item.match(reg5);
      // 小程序页卡的标题
      let title = item.match(reg6);
      // 小程序页卡的图片地址
      let imageurl = item.match(reg7);
      // 小程序的类型
      let type = item.match(reg8);
      // 小程序的servicetype
      let servicetype = item.match(reg9);
      let htmlTemplate = `<a href=\"${path[0].replace(reg3, '$1')}\"><img src=\"${imageurl[0].replace(reg7, '$1')}\" alt=\"跳转到小程序\" /></a>`
      return {
        mp_miniprogram: item,
        appid: appid[0].replace(reg2, '$1'),
        path: path[0].replace(reg3, '$1'),
        nickname: nickname[0].replace(reg4, '$1'),
        avatar: avatar[0].replace(reg5, '$1'),
        title: title[0].replace(reg6, '$1'),
        imageurl: imageurl[0].replace(reg7, '$1'),
        type: type[0].replace(reg8, '$1'),
        servicetype: servicetype[0].replace(reg9, '$1'),
        html: content.replace(reg1, htmlTemplate),
      }
    })
  }

  return result;

}

